INSERT [dbo].[Book] ([IdBook], [Title], [Author], [Description], [ImageUrl], [Subtitle], [Editorial], [PageCount], [Score]) VALUES (180, N'Gender and Female Villains in 21st Century Fairy Tale Narratives', N'Natalie Le Clue, Janelle Vermaak-Griessel', N'For every hero, there is a villain, and for every villain there is a story. But how much do we really know about the villain? Filling a gap in the field of gender representation and character evolution, the chapters in this edited collection focus on female villains in the fairy tale narratives of 21st Century media.', N'http://books.google.com/books/content?id=69tbEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', N'From Evil Queens to Wicked Witches', N'Emerald Group Publishing', 254, 5)
