INSERT [dbo].[Book] ([IdBook], [Title], [Author], [Description], [ImageUrl], [Subtitle], [Editorial], [PageCount], [Score]) VALUES (182, N'El Demonio de Arbennios', N'Bernard Torelló López', N'Kai es un antiguo soldado de élite que reside en Arbennios, capital del reino de Lénoda, cuya monótona vida se sustenta en tres pilares: un trabajo en la guardia del noble señor Nárenwal, la compañía de su leal grupo de amigos y las furtivas visitas de su amante secreta. Por desgracia, durante un caluroso día de verano tendrá lugar un ataque inesperado que dará un giro a su vida. Condenado por la misma sociedad a la que había defendido durante años, Kai se verá arrastrado por un torbellino de emociones y violencia que lo llevarán por el oscuro camino de la venganza.', N'http://books.google.com/books/content?id=Qc0QEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', NULL, N'Minotauro', 304, NULL)
