INSERT [dbo].[Book] ([IdBook], [Title], [Author], [Description], [ImageUrl], [Subtitle], [Editorial], [PageCount], [Score]) VALUES (260, N'Gender and Female Villains in 21st Century Fairy Tale Narratives', N'Natalie Le Clue, Janelle Vermaak-Griessel', N'For every hero, there is a villain, and for every villain there is a story. But how much do we really know about the villain? Filling a gap in the field of gender representation and character evolution, the chapters in this edited collection focus on female villains in the fairy tale narratives of 21st Century media.', N'http://books.google.com/books/content?id=69tbEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', N'From Evil Queens to Wicked Witches', N'Emerald Group Publishing', 254, 5)
INSERT [dbo].[Book] ([IdBook], [Title], [Author], [Description], [ImageUrl], [Subtitle], [Editorial], [PageCount], [Score]) VALUES (261, N'El Demonio de Arbennios', N'Bernard Torelló López', N'Kai es un antiguo soldado de élite que reside en Arbennios, capital del reino de Lénoda, cuya monótona vida se sustenta en tres pilares: un trabajo en la guardia del noble señor Nárenwal, la compañía de su leal grupo de amigos y las furtivas visitas de su amante secreta. Por desgracia, durante un caluroso día de verano tendrá lugar un ataque inesperado que dará un giro a su vida. Condenado por la misma sociedad a la que había defendido durante años, Kai se verá arrastrado por un torbellino de emociones y violencia que lo llevarán por el oscuro camino de la venganza.', N'http://books.google.com/books/content?id=Qc0QEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', NULL, N'Minotauro', 304, NULL)
