INSERT [dbo].[Book] ([IdBook], [Title], [Author], [Description], [ImageUrl], [Subtitle], [Editorial], [PageCount], [Score]) VALUES (383, N'Harry Potter y el prisionero de Azkaban / Harry Potter and the Prisoner of Azkaban', N'J. K. Rowling', N'Igual que en las dos primeras partes de la serie- La piedra filosofal y La cámara secreta- Harry aguarda con impaciencia el inicio del tercer cursp en el Colegio Hogwarts, Harry se pela con su bigotuda tía Marge, a la que convierte en globo, y debe huir en un autobús mágico. Mientras tanto, de la prisión de Azkaban se ha escapado un terrible villano, Sirius Black, un asesino en serie con poderes mágicos que fue cómplice de lord Voldemort y que parece dispuesto a eliminar a Harry del mapa. Y por si esto fuera poco, Harry deberá enfrentarse también a unos terribles monstuos, los dementores, seres abominables capaces de robarles al felicidad a los magos y de borrar todo recuerdo hermoso de aquellos que osan mirarlos. Lo que ninguno de estos malvados sabe es que Harry, con la ayuda de sus fieles amigos Ron y Hermione, es capaz de todo y mucho más.', N'http://books.google.com/books/content?id=6687PgAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', NULL, N'Salamandra Publicacions Y Edicions', 362, NULL)
