INSERT [dbo].[Book] ([IdBook], [Title], [Author], [Description], [ImageUrl], [Subtitle], [Editorial], [PageCount], [Score]) VALUES (480, N'Invencible', N'Robert Kirkman', N'This is an acclaimed and popular superhero series in the tradition of films like The Incredibles and Sky High. Mark Grayson is an ordinary teenager with a lousy part-time job, a fondness for Saturday morning cartoons and a clueless fascination with girls. But his family is anything but ordinary-his father happens to be Omni-man, the most powerful superhero on the planet! Mark is eager to fill his father''s very large shoes, and so when he starts to manifest powers of his own, he dons his own mantle as Invincible, Earth''s Newest Protector. Luckily, his dad is around to show him the ropes, at least he would be if he weren''t so busy saving the world all the time.', N'http://books.google.com/books/content?id=iV14AAAACAAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', N'Invincible', NULL, 0, NULL)
