INSERT [dbo].[Book] ([IdBook], [Title], [Author], [Description], [ImageUrl], [Subtitle], [Editorial], [PageCount], [Score]) VALUES (485, N'The Walking Dead: Woodbury', N'Robert Kirkman, Jay Bonansinga', N'Los caminantes se multiplican atraídos por un hambre salvaje de carne fresca. Paralizada por el miedo, Lilly confía en la protección que le dan los buenos samaritanos de Woodbury. Al principio, el lugar parece un santuario perfecto. Sus habitantes truecan servicios por comida y las barricadas se extienden. Lo mejor de todo es que el líder, Phillip Blake tiene a todos a raya. Pero las cosas no son lo que parecen. Blake, el Gobernador, tiene ideas particulares sobre la ley y el orden. Finalmente, Lilly y un grupo de rebeldes retarán el mandato del Gobernador y provocarán una masacre y la destrucción. Y así la carretera a Woodbury se convierte en el camino al infierno.', N'http://books.google.com/books/content?id=c1GmE8mvQ7QC&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', NULL, N'Grupo Planeta Spain', 309, NULL)
