INSERT [dbo].[Book] ([IdBook], [Title], [Author], [Description], [ImageUrl], [Subtitle], [Editorial], [PageCount], [Score]) VALUES (491, N'Jupiters legacy', N'Mark Millar, Frank Quitely', N'¡Mark Millar y Frank Quitely reinventan el cómic de superhéroes para el siglo XXI! En 1932, Sheldon Sampson sueña con una misteriosa fuente de poder que le lleva a él, a su hermano Walter y a un grupo de seguidores a una búsqueda a lo largo del mundo. Su descubrimiento cambiará el mundo para siempre. Décadas después, Sheldon y Walter son superhéroes de leyenda… ¡Pero sus descendientes no han sido capaces de mantenerse a la altura del mito!', N'http://books.google.com/books/content?id=dMt6DwAAQBAJ&printsec=frontcover&img=1&zoom=1&source=gbs_api', NULL, N'EDITORIAL PANINI MEXICO S.A DE C.V.', 141, NULL)
INSERT [dbo].[Book] ([IdBook], [Title], [Author], [Description], [ImageUrl], [Subtitle], [Editorial], [PageCount], [Score]) VALUES (492, N'Cuentos de buenas noches para niñas rebeldes', N'Elena Favilli, Francesca Cavallo', N'Historias poderosas de 110 mujeres extraordinarias Un clásico contemporáneo, Cuentos de buenas noches para niñas rebeldes reinventa los cuentos de hadas e inspira a las niñas con las historias de 110 mujeres extraordinarias: Cleopatra, Marie Curie, Frida Kahlo, Coco Chanel, Greta Thunberg, Taylor Swift... Cuenta con ilustraciones de 60 artistas femeninas de todos los rincones del mundo. Un libro en el que las niñas y las mujeres son las heroínas de sus propias historias. Publicado en 50 idiomas y en más de 110 países.', N'http://books.google.com/books/content?id=ivQuEQAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', N'Más de cien historias de mujeres extraordinarias', N'DK', 249, NULL)
INSERT [dbo].[Book] ([IdBook], [Title], [Author], [Description], [ImageUrl], [Subtitle], [Editorial], [PageCount], [Score]) VALUES (493, N'Cuentos de buenas noches para niñas rebeldes 3', N'Elena Favilli', N'Este nuevo volumen contiene 100 biografías de mujeres inmigrantes que se vieron obligadas a dejar su país de origen por varios motivos. Algunas buscaban mejores oportunidades, otras no tuvieron elección. Aquí encontrarás las historias de mujeres como Madeleine Albright, Asma Khan, Carmen Miranda o Rihanna. De chefs a cirujanas, pasando por músicas, políticas o campeonas deportivas. 100 nuevas historias que enseñarán a nuestras niñas a perseguir sus sueños, sin importar lo grandes que sean.', N'http://books.google.com/books/content?id=Gv4FEAAAQBAJ&printsec=frontcover&img=1&zoom=1&edge=curl&source=gbs_api', N'100 mujeres inmigrantes que han cambiado el mundo', N'Destino Infantil & Juvenil', 435, NULL)
